import React, { useState } from 'react'
import axios from 'axios';

export default function Register(props) {
    const [data, setdata] = useState({ login: '', email: '', full_name: '', password: '', password_confirmation: '' })
    const apiUrl = "http://localhost:8000/api/auth/register";
    const Regist = (e) => {
        e.preventDefault();
        const data1 = { login: data.login, email: data.email, full_name: data.full_name, password: data.password, password_confirmation: data.password_confirmation };
        axios.post(apiUrl, data1)
            .then((result) => {
                console.log(result.data);
                if (!result.data)
                    alert('Something wrong!');
                else
                    props.history.push('/main')
            })
    }
    // const handleChange = (e) => {
    //     e.persist();
    //     setdata({ ...data, [e.target.name]: e.target.value });
    // }
    handleChange = event => {
        this.setState({ name: event.target.value });
    }


    return (
        <div class="mx-auto">
            <form onSubmit={Regist} class="user">
                <center><svg xmlns="http://www.w3.org/2000/svg" width="50" height="70" fill="currentColor" class="bi bi-stars" viewBox="0 0 16 16">
                    <path d="M7.657 6.247c.11-.33.576-.33.686 0l.645 1.937a2.89 2.89 0 0 0 1.829 1.828l1.936.645c.33.11.33.576 0 .686l-1.937.645a2.89 2.89 0 0 0-1.828 1.829l-.645 1.936a.361.361 0 0 1-.686 0l-.645-1.937a2.89 2.89 0 0 0-1.828-1.828l-1.937-.645a.361.361 0 0 1 0-.686l1.937-.645a2.89 2.89 0 0 0 1.828-1.828l.645-1.937zM3.794 1.148a.217.217 0 0 1 .412 0l.387 1.162c.173.518.579.924 1.097 1.097l1.162.387a.217.217 0 0 1 0 .412l-1.162.387A1.734 1.734 0 0 0 4.593 5.69l-.387 1.162a.217.217 0 0 1-.412 0L3.407 5.69A1.734 1.734 0 0 0 2.31 4.593l-1.162-.387a.217.217 0 0 1 0-.412l1.162-.387A1.734 1.734 0 0 0 3.407 2.31l.387-1.162zM10.863.099a.145.145 0 0 1 .274 0l.258.774c.115.346.386.617.732.732l.774.258a.145.145 0 0 1 0 .274l-.774.258a1.156 1.156 0 0 0-.732.732l-.258.774a.145.145 0 0 1-.274 0l-.258-.774a1.156 1.156 0 0 0-.732-.732L9.1 2.137a.145.145 0 0 1 0-.274l.774-.258c.346-.115.617-.386.732-.732L10.863.1z" />
                </svg>
                    <h1 class="h3 mb-3 fw-normal">Please sign in</h1>
                    <div class="w-25 form-floating">
                        <input type="text" class="form-control" onChange={handleChange} value={data.login} id="floatingInput" placeholder="login"></input>
                        <label for="floatingInput">Login</label>
                    </div>
                    <div class="w-25 form-floating">
                        <input type="email" class="form-control" onChange={handleChange} value={data.email} id="floatingInput" placeholder="name@example.com"></input>
                        <label for="floatingInput">Email address</label>
                    </div>
                    <div class="w-25 form-floating">
                        <input type="text" class="form-control" onChange={handleChange} value={data.full_name} id="floatingInput" placeholder="full name"></input>
                        <label for="floatingInput">Full name</label>
                    </div>
                    <div class="w-25  form-floating">
                        <input type="password" class="form-control" onChange={handleChange} value={data.password} id="floatingPassword" placeholder="Password"></input>
                        <label for="floatingPassword">Password</label>
                    </div>
                    <div class="w-25  form-floating">
                        <input type="password" class="form-control" onChange={handleChange} value={data.password_confirmation} id="floatingPassword" placeholder="Confirm"></input>
                        <label for="floatingPassword">Confirm password</label>
                    </div>
                    <button class="w-25 p-3 btn btn-lg btn-primary" type="submit">Sign up</button>
                </center>

            </form>
        </div>


    );
}
