import React, { Component } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import LoadAvatar from "./LoadAvatar";

export default class ProfilePage extends Component {
    apiUrl = "http://localhost:8000/api/auth/logout";
    apiUser = "http://localhost:8000/api/users";
    onChange = event => {
        this.setState({ [event.target.name]: event.target.value });
    }

    Logout = event => {
        event.preventDefault();

        let token = Cookies.get('token');
        axios.defaults.headers.common['Authorization'] = 'Bearer ' + token;
        axios.post(this.apiUrl)
            .then((res) => {
                console.log(res);
                Cookies.remove('token');
                Cookies.remove('login');
                this.props.history.push('/login');
            })
    }



    render() {
        return (
            <div class="hello" >
                <p>Hello, {Cookies.get('login')}</p>
                <div class="mx-auto" >
                    <Router>
                        <Link to={'/avatarload'}><input type="button" class="btn btn-primary" value='Change Avatar' /></Link>
                        <Switch>
                            <Route path='/avatarload' component={LoadAvatar} />
                        </Switch>
                    </Router>
                    <form onSubmit={this.Logout}>
                        <button class="btn btn-primary" type="submit">Logout</button>
                    </form>
                </div>
            </div >

        );
    }
}
