import React, { Component } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';

export default class ProfilePage extends Component {
    apiUrl = "http://localhost:8000/api/auth/logout";
    apiUser = "http://localhost:8000/api/users";
    //////////////////////////
    Logout = event => {
        event.preventDefault();

        let token = Cookies.get('token');
        axios.defaults.headers.common['Authorization'] = 'Bearer ' + token;

        axios.post(this.apiUrl)
            .then((res) => {
                console.log(res);
                Cookies.remove('token');
                Cookies.remove('login');
                this.props.history.push('/login');
            })
    }
    ///////////////////////////////
    state = {
        user: [],
        imagefile: '',
    }
    componentDidMount() {
        let token = Cookies.get('token');
        axios.defaults.headers.common['Authorization'] = 'Bearer ' + token;
        let login = Cookies.get("login");
        axios.get(this.apiUser + "/" + login)
            .then(res => {
                const user = res.data;
                this.setState({ user });
                console.log(res);
                console.log(res.data);
            })
    }
    ///////////////////////////////
    LoadImg = event => {
        event.preventDefault();
        let formdata = new FormData();
        let login = Cookies.get('login');
        let token = Cookies.get('token');
        formdata.append('prof_pic', document.querySelector('#image').files[0]);
        axios.defaults.headers.common['Authorization'] = 'Bearer ' + token;
        axios.post(this.apiUser + "/avatar", formdata, {
            headers: {
                'Content-Type': 'multipart/form-data',
                'Accept': "application/json"
            }
        })
            .then((res) => {
                console.log(res);
            })

        axios.get(this.apiUser + "/" + login + "/avatar")
            .then(res => {
                console.log(res.data);
                const imagefile = res.data;
                this.setState({ imagefile });
            });
    }


    onChange = event => {
        this.setState({ [event.target.name]: event.target.value });
    }

    render() {
        return (
            <div class="hello" >
                <p class="form-group mx-sm-3 mb-2">Hello, {Cookies.get('login')}</p>
                <ul>
                    {this.state.user.map(person => <li>Full name:{person.full_name}</li>)}
                    {this.state.user.map(person => <li>E-mail: {person.email}</li>)}
                    {this.state.user.map(person => <li>E-mail: {"../../usof-2/public" + person.prof_pic}</li>)}
                    <img src={`data:image/jpeg;base64,${this.state.imagefile}`} />
                </ul>
                <div class="mx-auto" >
                    <form class="form-inline" onSubmit={this.LoadImg}>
                        <div class="form-group mx-sm-3 mb-2">
                            <input id='image' type="file" name="imagefile" onChange={this.onChange} class="w-25 form-control" />
                            <input type="submit" class="btn btn-primary" value='Change Avatar' />
                        </div>
                    </form>
                    <form class="form-group mx-sm-3 mb-2" onSubmit={this.Logout}>
                        <button class="btn btn-primary mb-1" type="submit">Logout</button>
                    </form>
                </div>
            </div >

        );
    }
}
