import React, { Component, createElement } from 'react';
import axios from 'axios'
import Cookies from 'js-cookie';

export default class Post_main extends Component {
    apiPost = "http://localhost:8000/api/posts";
    state = {
        post: [],
        cat: [],
    }
    componentDidMount() {
        let token = Cookies.get('token');
        axios.defaults.headers.common['Authorization'] = 'Bearer ' + token;
        axios.get(this.apiPost + "/" + Cookies.get('title_post'))
            .then(res => {
                const post = res.data;
                this.setState({ post });
                console.log(res.data);
            })
        axios.get(this.apiPost + "/" + Cookies.get('title_post') + '/categories')
            .then(res => {
                let tmp = res.data[0];
                for (let i = 0; i == i; i++) {
                    if (tmp[i] !== undefined) {
                        document.getElementById('category').innerHTML = tmp[i].title;
                        break;
                    }
                }

            })
    }
    render() {
        return (
            <div>
                <div class="shadow-sm p-3 mb-5 bg-white rounded card m-5 card-body d-flex flex-column">
                    <div class="card-body">
                        <div class="media mb-3">
                            <div class="media-body ml-3">
                                <h3>{this.state.post.map(info => info.title)}</h3>
                                <div class="text-muted big">{this.state.post.map(info => info.author)}</div>
                                <div id="category" class="text-muted small">{this.state.cat.map(info => info.title)}</div>
                            </div>
                        </div>
                        <p>{this.state.post.map(info => info.content)}</p>
                    </div>
                    <div class="card-footer">
                        <a class="d-inline-block text-muted ml-3 p-2"> <strong>0</strong> Likes </a>
                        <a class="d-inline-block text-muted ml-3"> <strong>0</strong> Comments </a>
                    </div>
                </div>

                <div>
                    <h3 class="m-5">Answers:</h3>
                    <button type="button" class="btn btn-outline-info" style={{ marginLeft: "5rem" }}>Create comment</button>
                    <div class="col-md-8 shadow-sm p-3 mb-5 bg-white rounded card m-5 card-body d-flex flex-column">
                        <div class="media g-mb-30 media-comment">
                            <img class="d-flex g-width-50 g-height-50 rounded-circle g-mt-3 g-mr-15"></img>
                            <div class="media-body u-shadow-v18 g-bg-secondary g-pa-30">
                                <div class="g-mb-15">
                                    <h5 class="h5 g-color-gray-dark-v1 mb-0">Login</h5>
                                    <span class="g-color-gray-dark-v4 g-font-size-12">Current data</span>
                                </div>

                                <p>Content</p>

                                <ul class="list-inline d-sm-flex my-0">
                                    <li class="list-inline-item g-mr-20">
                                        <a class="u-link-v5 g-color-gray-dark-v4 g-color-primary--hover"> <strong>0</strong> Likes </a>

                                    </li>
                                </ul>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
